import React, { useContext } from 'react';
import CountContext from './CountContext';

const CounterControls = () => {
  const { batata } = useContext(CountContext);

  return (
    <div>
      <button onClick={() => batata({ type: 'increment' })}>Incrementar</button>
      <button onClick={() => batata({ type: 'decrement' })}>Decrementar</button>
    </div>
  );
};

export default CounterControls;
